            var ctx = this.self.context.projectionContext;
            var running = ctx.runningPolicyValues;
            var months = running.months;
            var year = running.year;
            var proposal = ctx.proposal;

            var totalFa = this.self.totalFaceAmount(args);
            var debugLayerList = [];
