        var minIssueAge = _V(_PV(schema, "ProductSchema.BasicParticular.IssueAge.Min"));
        var maxIssueAge = _V(_PV(schema, "ProductSchema.BasicParticular.IssueAge.Max"));
        var isBasePlan = catalog.call("isBasePlan");
